      let reader = new FileReader();

      reader.onload = () => {
        const opt = {
          w: 178,
          h: 178,
          quality: 0.92
        };
        let img = new Image();
        let canvas = document.createElement('canvas');
        canvas.width = opt.w;
        canvas.height = opt.h;
        let ctx = canvas.getContext('2d');

        img.onload = async () => {
          ctx.drawImage(img, 0, 0, img.width, img.height, 0, 0, opt.w, opt.h);
          await canvas.toBlob(blob => {
            this.uploadImg = blob;
          });
          this.imageUrl = await canvas.toDataURL('image/jpeg', opt.quality);
        };

        img.src = reader.result;
      };

      reader.readAsDataURL(file.raw);
    },

    upload() {
      this.$refs.upload.submit();
    },

    getUser() {
      this.$axios.get('getUser', {
        params: {
          account: this.user.account
        }
      }).then(res => {
        this.userMsg = res.data;
      }).catch(err => {
        console.log(err);
      });
    },

    submit() {
      this.$axios.put('editUser', this.userMsg).then(res => {
        if (res) {
          this.$message({
            message: '修改成功',
            type: 'success',
            duration: 1000
          });
        } else {
          this.$message({
            message: '修改失败',
            type: 'error',
            duration: 1000
          });
        }
      }).catch(err => {
        console.log(err);
      });
    }

  }
